"""backprop_network.py"""import randomimport numpy as npimport mathimport matplotlib.pyplot as pltclass Network(object):    def __init__(self, sizes):        """The list ``sizes`` contains the number of neurons in the        respective layers of the network.  For example, if the list        was [2, 3, 1] then it would be a three-layer network, with the        first layer containing 2 neurons, the second layer 3 neurons,        and the third layer 1 neuron.  The biases and weights for the        network are initialized randomly, using a Gaussian        distribution with mean 0, and variance 1.  Note that the first        layer is assumed to be an input layer, and by convention we        won't set any biases for those neurons, since biases are only        ever used in computing the outputs from later layers."""        self.num_layers = len(sizes)        self.sizes = sizes        self.biases = [np.random.randn(y, 1) for y in sizes[1:]]        self.weights = [np.random.randn(y, x)                        for x, y in zip(sizes[:-1], sizes[1:])]    def SGD(self, training_data, epochs, mini_batch_size, learning_rate,            test_data):        """Train the neural network using mini-batch stochastic        gradient descent.  The ``training_data`` is a list of tuples        ``(x, y)`` representing the training inputs and the desired        outputs.  """        all_grad_norms = [] # bias gradients euclidean norms         for i in range(len(self.biases)):            all_grad_norms.append([])        test_acc = [self.one_label_accuracy(test_data)]        print("Initial test accuracy: {0}".format(test_acc[-1]))        # train_acc = [self.one_hot_accuracy(training_data)]        # loss = [self.loss(training_data)]        n = len(training_data)        for j in range(epochs):            random.shuffle(list(training_data))            mini_batches = [                training_data[k:k+mini_batch_size]                for k in range(0, n, mini_batch_size)]            grad_norms = [0 for i in range(len(self.biases))] # per epoch            for mini_batch in mini_batches:                grad_norms_add = self.update_mini_batch(mini_batch, learning_rate)                for i in range(len(grad_norms_add)):                    grad_norms[i] += grad_norms_add[i]            for i in range(len(grad_norms)):                grad_norms[i] /= n                all_grad_norms[i].append(grad_norms[i])            test_acc.append(self.one_label_accuracy(test_data))            print ("Epoch {0} test accuracy: {1}".format(j, test_acc[-1]))            # train_acc.append(self.one_hot_accuracy(training_data))            # loss.append(self.loss(training_data))        x_vals = [i for i in range(1, epochs + 1)]        x_ticks = [i for i in range(0, epochs + 1, int(epochs / 10))]        props = ['r--','g--','b--','c--','m--'] # assume 4 hidden layers + output layer        textstr = "\n".join(['red - layer 1','green - layer 2', 'blue - layer 3', \            'cyan -layer 4', 'magenta - layer 5'])        _, ax = plt.subplots()        for i in range(len(self.biases)):            plt.plot(x_vals, all_grad_norms[i], props[i])        plt.xticks(x_ticks)        plt.xlabel('epoch')        plt.ylabel('gradients euclidean norms')        plt.text(0.5, 0.95, textstr, transform=ax.transAxes, verticalalignment='top')        plt.show()        """        x_vals = [i for i in range(0, epochs + 1)]        x_ticks = [i for i in range(0, epochs + 1, int(epochs / 10))]        plt.plot(x_vals, test_acc, 'r--')        plt.xticks(x_ticks)        plt.text(epochs / 2, 0.5, 'test accuracy')        plt.xlabel('epoch')        plt.ylabel('test accuracy')        plt.show()        plt.plot(x_vals, train_acc, 'b--')        plt.xticks(x_ticks)        plt.text(epochs / 2, 0.5, 'train accuracy')        plt.xlabel('epoch')        plt.ylabel('train accuracy')        plt.show()        plt.plot(x_vals, loss, 'g--')        plt.xticks(x_ticks)        plt.text(epochs / 2, min(loss) + (max(loss) - min(loss)) / 2, 'training loss')        plt.xlabel('epoch')        plt.ylabel('train loss')        plt.show()        """    def update_mini_batch(self, mini_batch, learning_rate):        """Update the network's weights and biases by applying        stochastic gradient descent using backpropagation to a single mini batch.        The ``mini_batch`` is a list of tuples ``(x, y)``."""        nabla_b = [np.zeros(b.shape) for b in self.biases]        nabla_w = [np.zeros(w.shape) for w in self.weights]        for x, y in mini_batch:            delta_nabla_b, delta_nabla_w = self.backprop(x, y)            nabla_b = [nb+dnb for nb, dnb in zip(nabla_b, delta_nabla_b)]            nabla_w = [nw+dnw for nw, dnw in zip(nabla_w, delta_nabla_w)]                grad_norms = []        for nb in nabla_b:            grad_norms.append(np.linalg.norm(nb))               self.weights = [w - (learning_rate / len(mini_batch)) * nw                        for w, nw in zip(self.weights, nabla_w)]        self.biases = [b - (learning_rate / len(mini_batch)) * nb                       for b, nb in zip(self.biases, nabla_b)]        return grad_norms                           def backprop(self, x, y):        """The function receives as input a 784 dimensional         vector x and a one-hot vector y.        The function should return a tuple of two lists (db, dw)         as described in the assignment pdf. """        dw = []        db = []        n_layers = len(self.sizes) - 1        t = 1 # the layer index        z = [x] # neurons values after activation        v = [0] # neurons values before activation        for b, w in zip(self.biases, self.weights):            v.append(np.dot(w, z[t - 1]) + b)            if t < n_layers:                z.append(sigmoid(v[t]))            else:                break            t += 1        prev = self.loss_derivative_wr_output_activations(v[t], y) # previous derivative        w = [0] + self.weights        while t >= 1:            if t < n_layers:                 prev = sigmoid_derivative(v[t]) * prev            dw.append(np.dot(prev, z[t - 1].transpose()))            db.append(prev)            prev = np.dot(w[t].transpose(), prev)            t -= 1        dw.reverse()        db.reverse()                   return db, dw    def one_label_accuracy(self, data):        """Return accuracy of network on data with numeric labels"""        output_results = [(np.argmax(self.network_output_before_softmax(x)), y)         for (x, y) in data]        return sum(int(x == y) for (x, y) in output_results)/float(len(data))    def one_hot_accuracy(self,data):        """Return accuracy of network on data with one-hot labels"""        output_results = [(np.argmax(self.network_output_before_softmax(x)), np.argmax(y))                          for (x, y) in data]        return sum(int(x == y) for (x, y) in output_results) / float(len(data))    def network_output_before_softmax(self, x):        """Return the output of the network before softmax if ``x`` is input."""        layer = 0        for b, w in zip(self.biases, self.weights):            if layer == len(self.sizes) - 1:                x = np.dot(w, x) + b            else:                x = sigmoid(np.dot(w, x) + b)            layer += 1        return x    def loss(self, data):        """Return the loss of the network on the data"""        loss_list = []        for (x, y) in data:            net_output_before_softmax = self.network_output_before_softmax(x)            net_output_after_softmax = self.output_softmax(net_output_before_softmax)            loss_list.append(np.dot(-np.log(net_output_after_softmax).transpose(),y).flatten()[0])        return sum(loss_list) / float(len(data))    def output_softmax(self, output_activations):        """Return output after softmax given output before softmax"""        output_exp = np.exp(output_activations)        return output_exp/output_exp.sum()    def loss_derivative_wr_output_activations(self, output_activations, y):        """Return derivative of loss with respect to the output activations before softmax"""        return self.output_softmax(output_activations) - ydef sigmoid(z):    """The sigmoid function."""    return 1.0/(1.0+np.exp(-z))def sigmoid_derivative(z):    """Derivative of the sigmoid function."""    return sigmoid(z)*(1-sigmoid(z))